use_bpm 60

e_string = ""


C_offset = 0
D_offset = 2
E_offset = 4
F_offset = 5
G_offset = 7
A_offset = 9
B_offset = 11
C_up_offset = 12

C4_OFFSET = 60
C5_OFFSET = 72

index = 0
e_digit = 0
value = 0

define :set_value do
  if e_digit == 0
    value = C_offset
  end
  if e_digit == 1
    value = D_offset
  end
  if e_digit == 2
    value = E_offset
  end
  if e_digit == 3
    value = F_offset
  end
  if e_digit == 4
    value = G_offset
  end
  if e_digit == 5
    value = A_offset
  end
  if e_digit == 6
    value = B_offset
  end
  if e_digit == 7
    value = C_up_offset
  end
end


live_loop :e do
  e_digit = e_string[index].to_i
  set_value
  value_c4_scale = value + C4_OFFSET
  value_c5_scale = value + C5_OFFSET
  index = index + 1
  # loops the 10000 digit of e forever
  if index > 9999
    index = 9999
  end
  
  play value_c4_scale
  play value_c5_scale
  
  #sleep 1/e seconds before playing next note
  sleep 0.367
end




