use_bpm 60

pi_string = "3."



index = 0
pi_digit = 0
value = 0
value_c4_scale = :C4
value_c5_scale = :C5

define :set_value do
  case pi_digit
  when 0
    value_c4_scale = :C4
    value_c5_scale = :C5
  when 1
    value_c4_scale = :D4
    value_c5_scale = :D5
  when 2
    value_c4_scale = :E4
    value_c5_scale = :E5
  when 3
    value_c4_scale = :F4
    value_c5_scale = :F5
  when 4
    value_c4_scale = :G4
    value_c5_scale = :G5
  when 5
    value_c4_scale = :A4
    value_c5_scale = :A5
  when 6
    value_c4_scale = :B4
    value_c5_scale = :B5
  when 7
    value_c4_scale = :C5
    value_c5_scale = :C6
  end
end


live_loop :pi do
  pi_digit = pi_string[index].to_i
  set_value
  index = index + 1
  # loops the 10000 digit of pi forever
  if index > 9999
    index = 9999
  end
  
  play value_c4_scale
  play value_c5_scale
  
  #sleep 1/pi seconds before playing next note
  sleep 0.31830988618
end




